import React,{useState} from "react";
import Header from "./Components/Header/Header";
import Sidebar from "./Components/Sidebar/Sidebar";
import Footer from "./Components/Footer/Footer";
import Home from "./Components/Home/Home";
import { Grid, ThemeProvider } from "@mui/material";
import { useMediaQuery, useTheme } from "@material-ui/core";
import FontTheme from "./Themes/FontTheme";
import Tryon from "./Components/Tryon/Tryon";
import Login from "./Components/Login/Login";
import FirstLogin from "./Components/Login/FirstLogin";
import { BrowserRouter as Router, Route, Routes} from "react-router-dom";
import { HOME, TRYON, LOGIN, FIRSTLOGIN } from "./Routes/Routes";
import { useSelector } from "react-redux";
import "./App.css";


const App = () => {
  const theme = useTheme();
  const [path,setPath]=useState("login");
  useSelector((store)=>setPath(store.DataReducer[0]));
  const isMobile = useMediaQuery(theme.breakpoints.down("xs"));
  const fontTheme = FontTheme();

  return (
    <>
    {console.log(path)}

      <Router>
        <ThemeProvider theme={fontTheme}>
          {path!=="login" && <Header />}
          <Grid container spacing={2}>
            <Grid item xs={12} sm={9}>
              <Routes>
                <Route exact path={TRYON} element={<Tryon />} />
                <Route exact path={HOME} element={<Home />} />
              </Routes>
            </Grid>
            <Routes>
              <Route exact path={LOGIN} element={<Login />} />
             <Route exact path={FIRSTLOGIN} element={<FirstLogin />} />
            </Routes>
            <Grid item xs={0} sm={3}>
              {path !=="login" &&
                !isMobile && <Sidebar />}
            </Grid>
          </Grid>
          <Footer />
        </ThemeProvider>
      </Router>
    </>
  );
};

export default App;
