import * as React from 'react';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import { useTheme,styled } from '@material-ui/core';
import { useMediaQuery } from '@material-ui/core';
import AccountCircle from '@mui/icons-material/AccountCircle';
import Switch from '@mui/material/Switch';
import FormControlLabel from '@mui/material/FormControlLabel';
import FormGroup from '@mui/material/FormGroup';
import MenuItem from '@mui/material/MenuItem';
import Menu from '@mui/material/Menu';
import Stack from "@mui/material/Stack";
import { ThemeProvider } from '@emotion/react';
import {Icon} from "../../Themes/HeaderStyle";

export default function Heeder() {
  const [auth, setAuth] = React.useState(true);
  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleChange = (event) => {
    setAuth(event.target.checked);
  };

  const handleMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };
  const theme= useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('xs'));


theme.typography.h4 = {
  fontSize: '1.4rem',
  [theme.breakpoints.up('sm')]: {
    fontSize: '2rem',
  },

};
theme.typography.h6 = {
  fontSize: '0.7rem',
  [theme.breakpoints.up('sm')]: {
    fontSize: '1rem',
  },
};



  return (
    <ThemeProvider theme={theme}>
    <Box sx={{ flexGrow: 1 }}>

      <AppBar position="static" sx={{bgcolor:"#191919",height:90}}>
        <Toolbar sx={{height:80,display:"flex",justifyContent: 'space-between'}}>
       <Stack sx={{ml:1,mt:1}}>
          <Typography variant="h4" sx={{ fontWeight:"bold"}}>
            BRIDAL CONCEPT
          </Typography>
          <Typography variant="h6" align="center" sx={{fontWeight:"bold"}}>
            HAUTE COUTURE
          </Typography>
          </Stack>

            {isMobile?<Box>
              <IconButton
                size="large"
                aria-label="account of current user"
                aria-controls="menu-appbar"
                aria-haspopup="true"
                onClick={handleMenu}
                color="inherit"
              >
               <MenuIcon/>
              </IconButton>
              <Menu
                id="menu-appbar"
                anchorEl={anchorEl}
                anchorOrigin={{
                  vertical: 'top',
                  horizontal: 'right',
                }}
                keepMounted
                transformOrigin={{
                  vertical: 'top',
                  horizontal: 'right',
                }}
                open={Boolean(anchorEl)}
                onClose={handleClose}
              >
                <MenuItem onClick={handleClose}>Profile</MenuItem>
                <MenuItem onClick={handleClose}>My account</MenuItem>
              </Menu>
            </Box>:<Stack direction="row">

              <Icon sx={{border:1,bordercolor:"#000000"}}>
                C
              </Icon>
              <Icon sx={{border:1,bordercolor:"#000000"}}>
                M
              </Icon>
              <Icon sx={{border:1,bordercolor:"#000000"}}>
                N
              </Icon>
              </Stack>}


        </Toolbar>
      </AppBar>




    </Box>
    </ThemeProvider>   
  );
}
