import React,{useState,useEffect} from "react";
import Header from "./Components/Header/Header";
import Sidebar from "./Components/Sidebar/Sidebar";
import Footer from "./Components/Footer/Footer";
import Home from "./Routes/Home/Home";
import { Grid, ThemeProvider } from "@mui/material";
import { useMediaQuery, useTheme } from "@material-ui/core";
import FontTheme from "./Themes/FontTheme";
import Tryon from "./Components/Tryon/Tryon";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import { ProgressBar } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";

const App = () => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down("xs"));
  const fontTheme = FontTheme();
  const [loader,setLoader]=useState(true);
  const [loaderValue,setLoaderValue]=useState(25);
  useEffect(()=>{

    setTimeout(()=>{
      setLoaderValue(50);

      setTimeout(()=>{
          setLoaderValue(75);
      },1000);
      setTimeout(()=>{
              setLoaderValue(100);

      },2000);
      setTimeout(()=>{
                 setLoader(false);
      },1000)
    },1000)



  },[])

  return (
    <>
      <Router>
        <ThemeProvider theme={fontTheme}>
          <Header />
          {(loader)&&<ProgressBar now={loaderValue} />}
          <Grid container spacing={2}>
            <Grid item xs={12} sm={9}>
              <Routes>
                <Route exact path="/tryonappointment" element={<Tryon />} />
                <Route exact path="/" element={<Home />} />
              </Routes>
            </Grid>
            <Grid item xs={0} sm={3}>
              {!isMobile && <Sidebar />}
            </Grid>
          </Grid>
          <Footer />
        </ThemeProvider>
      </Router>
    </>
  );
};

export default App;
