import React from "react";

const Notification = ({ icon, title, time, date }) => {
  return (
    <>
      <div className="container flex items-center border-2  border-gray-900 rounded-md justify-start cursor-pointer hover:border-gray-200">
        <div className="icon sm:mr-5">{icon}</div>
        <div className="details ">
          <h1 className="sm:text-sm lg:text-lg text-xs font-lighter text-center mr-1">
            {title}
          </h1>
          <div className="date flex mr-1 sm:mr-0 mt-1">
            <h2 className="sm:mr-2 mr-1 sm:text-sm text-xs font-thin">
              {time}
            </h2>
            <h2 className="sm:text-sm font-bold text-xs ">{date}</h2>
          </div>
        </div>
      </div>
    </>
  );
};
export default Notification;
