import React,{useState,useEffect} from 'react';
import { Box,Grid,Typography,Stack, createTheme,ThemeProvider, useTheme,useMediaQuery} from '@mui/material';
import {NotificationsActiveOutlined} from '@mui/icons-material';
const Notification = () => {

    const [isMobile,setIsMobile]=useState("false");
    useEffect=(()=>{

        setIsMobile(useMediaQuery(theme.breakpoints.down('xs')));
    },[])




    const theme=createTheme();
    theme.typography.h6 = {
        fontSize: '0.6rem',
        [theme.breakpoints.up('sm')]: {
          fontSize: '1rem',
        },
      };
      theme.typography.caption={
          fontSize:'0.5rem',
          [theme.breakpoints.up('sm')]:{
              fontSize:'1rem'
          }
      }


    return (
        <>
          <Box sx={{width:"90%",border:3,borderColor:"gray.900",borderRadius: 1,mx:"auto",minHeight:50,my:2,px:1,py:1,overflow:"hidden"}}>
<Grid container alignItems="center"
  justifyContent="center">
    <Grid item xs={3} md={2} >

        {(isMobile)
        ?<NotificationsActiveOutlined />
        :<NotificationsActiveOutlined fontSize="large"/>}



    </Grid>


<Grid item xs={9} md={10}>

<ThemeProvider theme={theme}>

<Typography variant="h6" alignLeft sx={{fontWeight:"bold"}}>Upload Requested Document</Typography>
<Stack direction="row" sx={{mt:1}}>
   <Typography variant="caption">9:30 am</Typography>
   <Typography variant="caption" sx={{ml:1,fontWeight:"bold"}}>Due Soon</Typography>
   </Stack>

</ThemeProvider>



</Grid>

</Grid>    
              </Box> 
        </>
    )
}

export default Notification;
